package com.wearadaptive.grpcchat.app;

import com.wearadaptive.grpcchat.list.LinkedList;

import static com.wearadaptive.grpcchat.utilities.StringUtils.join;
import static com.wearadaptive.grpcchat.utilities.StringUtils.split;
import static com.wearadaptive.grpcchat.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
